/* eslint-disable react/react-in-jsx-scope */
/* eslint-disable react/jsx-filename-extension */
import Image from 'next/image';
import Link from 'next/link';
import Layout from '../components/Layout';
import fetchFromCMS from '../lib/service';

export default function Home({ portfolioItems }) {
  return (
    <Layout>
      <div className="entries">
        <div className="row justify-content-start  ">
          {portfolioItems.map((portfolio) => (
            <div className="col-md-6">
              <div className="entry mb-3">
                <Link as={`/portfolio/${portfolio.slug}`} href="/portfolio/[id]">
                  <div className="main-image">
                    <Image
                      src={portfolio.image.data.name}
                      width={600}
                      height={400}
                      alt={portfolio.Headline}
                    />
                    <h1>{portfolio.Headline}</h1>
                  </div>
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
}

export async function getStaticProps() {
  const portfolioItemsData = await fetchFromCMS('portfolios');
  const portfolioItems = await portfolioItemsData;
  console.log('beep: ', portfolioItems);
  return {
    props: { portfolioItems },
    revalidate: 1,
  };
}
